function defineCollection(collection: Collection): DefinedCollection

type Collection = {
  // 确定内容如何处理
  type: 'page' | 'data'
  // 指定内容位置
  source?: string | CollectionSource
  // 用于内容验证和类型的 Zod 模式
  schema?: ZodObject<T>
  // 用于查询优化的数据库索引
  indexes?: CollectionIndex[]
}

type CollectionIndex = {
  // 要包含在索引中的列名
  columns: string[]
  // 可选的自定义索引名称
  name?: string
  // 这是否是唯一索引（默认：false）
  unique?: boolean
}

type CollectionSource = {
  // 用于内容匹配的 Glob 模式
  include: string
  // .path 前缀（仅适用于 'page' 类型）
  prefix?: string
  // 用于排除内容的 Glob 模式
  exclude?: string[]
  // 用于内容匹配的根目录
  cwd?: string
  // 远程 git 仓库 URL（例如，https://github.com/nuxt/content）
  repository?: string
  // 私有仓库的认证令牌（例如，GitHub 个人访问令牌）
  authToken?: string
}

